import * as React from 'react';
import { useState } from 'react';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { Button } from '@mui/material';
import TextField from '@mui/material/TextField';
import { Link, useLocation } from 'react-router-dom';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import EditIcon from '@mui/icons-material/Edit';
import { Tooltip } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { styled, Box } from '@mui/system';
import ModalUnstyled from '@mui/base/ModalUnstyled';
import { Stack } from '@mui/material';

const data = [
        {
                id: 1,
                firstName: 'Winter',
                lastName: 'MacRoy',
                relationship: 'Spouse'
        },
        {
                id: 2,
                firstName: 'Lucy',
                lastName: 'MacRoy',
                relationship: 'Child'
        },
]

const relationshipTypes = [
        {
                id: 1,
                relationshipType: 'Child'
        },
        {
                id: 2,
                relationshipType: 'Spouse'
        },
        {
                id: 3,
                relationshipType: 'Domestic Partner'
        },
]

const StyledModal = styled(ModalUnstyled)`
  position: fixed;
  z-index: 1300;
  right: 0;
  bottom: 0;
  top: 0;
  left: 0;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Backdrop = styled('div')`
  z-index: -1;
  position: fixed;
  right: 0;
  bottom: 0;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.5);
  -webkit-tap-highlight-color: transparent;
`;

const style = {
        width: 400,
        bgcolor: 'white',
        border: '2px solid #000',
        p: 2,
        px: 4,
        pb: 3,
};


const Employee = (props) => {
        const { state } = useLocation();
        const [open, setOpen] = useState(false);
        const [dependantFirstName, setDependantFirstName] = useState("");
        const [dependantLastName, setDependantLastName] = useState("");
        const [dependantRelationship, setDependantRelationship] = useState("");
        const [modalModifyType, setModalModifyType] = useState("");
        const handleOpen = () => setOpen(true);
        const handleClose = () => setOpen(false);

        const DeleteDependant = () => {
                alert('deleted');
        }

        const AddDependant = () => {
                prepareModal({}, "Add");
        }

        const EditDependant = (row) => {
                prepareModal(row, "Edit");
        }

        function prepareModal(modalRow, type) {
                setModalModifyType(type);
                setDependantFirstName(modalRow.firstName);
                setDependantLastName(modalRow.lastName);
                setDependantRelationship(modalRow.relationship);
                handleOpen();
        }

        const EditEmployee = () => {
                alert('Employee Editted');
        }


        return (
                <div>
                        <Box component="form"
                                sx={{
                                        '& .MuiTextField-root': { m: 1, width: '25ch' },
                                        'flowGrow': 1
                                }}>
                                <StyledModal
                                        aria-labelledby="unstyled-modal-title"
                                        aria-describedby="unstyled-modal-description"
                                        open={open}
                                        onClose={handleClose}
                                        BackdropComponent={Backdrop}
                                >
                                        <Box sx={style}>
                                                <h1>{modalModifyType} Dependant</h1>
                                                <Grid container spacing={3}>
                                                        <Grid item xs={12}>
                                                                <TextField
                                                                        required
                                                                        id="outlined-required"
                                                                        label="First Name"
                                                                        value={dependantFirstName}
                                                                />
                                                        </Grid>
                                                        <Grid item xs={12}>
                                                                <TextField
                                                                        required
                                                                        id="outlined-required"
                                                                        label="Last Name"
                                                                        value={dependantLastName}
                                                                />
                                                        </Grid>
                                                        <Grid item xs={12}>
                                                                <TextField
                                                                        required
                                                                        id="outlined-required"
                                                                        label="Relationship"
                                                                        value={dependantRelationship}
                                                                />
                                                        </Grid>
                                                        <Grid item xs={12}>
                                                                <Stack direction="row" spacing={4}>
                                                                        <Button variant="contained" color="success">Update</Button>
                                                                        <Button variant="contained" color="secondary" onClick={handleClose}>Cancel</Button>
                                                                </Stack>
                                                        </Grid>
                                                </Grid>
                                        </Box>
                                </StyledModal>
                                <Grid container spacing={2}>
                                        <Grid item xs={12} sx={{ display: 'flex' }}>
                                                <div>
                                                        <Link to="/employees"><Button startIcon={<ArrowBackIcon />} size="large">Go Back</Button></Link>
                                                </div>

                                        </Grid>
                                        <Grid item xs={6}>
                                                <Grid item xs={12}>
                                                        <h1>Edit Employee</h1>
                                                        <TextField
                                                                required
                                                                id="outlined-required"
                                                                label="First Name"
                                                                defaultValue={state.firstName}
                                                        />
                                                        <TextField
                                                                required
                                                                id="outlined-required"
                                                                label="Last Name"
                                                                defaultValue={state.lastName}
                                                        />
                                                </Grid>
                                                <Grid item xs={12}>
                                                        <Button variant="contained" color="success" size='large' onClick={EditEmployee}>Update</Button>
                                                </Grid>
                                        </Grid>
                                        <Grid item xs={6}>
                                                <Grid item xs={12}>
                                                        <h2>Dependants <Tooltip title="Add Dependant"><Button onClick={AddDependant} sx={{ float: 'right', marginRight: "20px" }}><AddCircleIcon color="success" fontSize='large' /></Button></Tooltip> </h2>
                                                </Grid>
                                                <Grid item xs={12}>
                                                        <TableContainer component={Paper}>
                                                                <Table sx={{ minWidth: 650 }} aria-label="simple table">
                                                                        <TableHead>
                                                                                <TableRow>
                                                                                        <TableCell>First Name</TableCell>
                                                                                        <TableCell>Last Name</TableCell>
                                                                                        <TableCell>Relationship</TableCell>
                                                                                        <TableCell>Options</TableCell>
                                                                                </TableRow>
                                                                        </TableHead>
                                                                        <TableBody>
                                                                                {data.map((row) => (
                                                                                        <TableRow
                                                                                                key={row.id}
                                                                                        >
                                                                                                <TableCell component="th" scope="row">
                                                                                                        {row.firstName}
                                                                                                </TableCell>
                                                                                                <TableCell>{row.lastName}</TableCell>
                                                                                                <TableCell>{row.relationship}</TableCell>
                                                                                                <TableCell><Tooltip title="Edit Dependant"><Button onClick={e => EditDependant(row)}><EditIcon /></Button></Tooltip><Tooltip title="Delete Dependant"><Button onClick={DeleteDependant}><DeleteIcon /></Button></Tooltip></TableCell>
                                                                                        </TableRow>
                                                                                ))}
                                                                        </TableBody>
                                                                </Table>
                                                        </TableContainer>
                                                </Grid>
                                        </Grid>
                                </Grid>
                        </Box>
                </div>
        );
}

export default Employee
