import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { Button } from '@mui/material';
import TextField from '@mui/material/TextField';
import { Link, useLocation } from 'react-router-dom';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import EditIcon from '@mui/icons-material/Edit';
import { Tooltip } from '@mui/material';
import { Modal } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

const data = [
        {
                id: 1,
                firstName: 'Winter',
                lastName: 'MacRoy',
                relationship: 'Spouse'
        },
        {
                id: 2,
                firstName: 'Lucy',
                lastName: 'MacRoy',
                relationship: 'Child'
        },
]

const [open, setOpen] = React.useState(false);
const handleOpen = () => setOpen(true);
const handleClose = () => setOpen(false);

const DeleteDependant = () => {
        alert('deleted');
}

const AddDependant = () => {
        alert('Added');
}

const EditDependant = () => {
        alert('Editted');
}

const EditEmployee = () => {
        alert('Employee Editted');
}

const Employee = (props) => {
        const { state } = useLocation();
        return (
                <div>
                        <Box component="form"
                                sx={{
                                        '& .MuiTextField-root': { m: 1, width: '25ch' },
                                        'flowGrow': 1
                                }}>
                                <Grid container spacing={2}>
                                        <Grid item xs={12} sx={{ display: 'flex' }}>
                                                <div>
                                                        <Link to="/employees"><Button startIcon={<ArrowBackIcon />} size="large">Go Back</Button></Link>
                                                </div>

                                        </Grid>
                                        <Grid item xs={6}>
                                                <Grid item xs={12}>
                                                        <h1>Edit Employee</h1>
                                                        <TextField
                                                                required
                                                                id="outlined-required"
                                                                label="First Name"
                                                                defaultValue={state.firstName}
                                                        />
                                                        <TextField
                                                                required
                                                                id="outlined-required"
                                                                label="Last Name"
                                                                defaultValue={state.lastName}
                                                        />
                                                </Grid>
                                                <Grid item xs={12}>
                                                        <Button variant="contained" color="success" size='large' onClick={EditEmployee}>Update</Button>
                                                </Grid>
                                        </Grid>
                                        <Grid item xs={6}>
                                                <Grid item xs={12}>
                                                        <h2>Dependants <Tooltip title="Add Dependant"><Button onClick={AddDependant} sx={{ float: 'right', marginRight: "20px" }}><AddCircleIcon color="success" fontSize='large' /></Button></Tooltip> </h2>
                                                </Grid>
                                                <Grid item xs={12}>
                                                        <TableContainer component={Paper}>
                                                                <Table sx={{ minWidth: 650 }} aria-label="simple table">
                                                                        <TableHead>
                                                                                <TableRow>
                                                                                        <TableCell>First Name</TableCell>
                                                                                        <TableCell>Last Name</TableCell>
                                                                                        <TableCell>Relationship</TableCell>
                                                                                        <TableCell>Options</TableCell>
                                                                                </TableRow>
                                                                        </TableHead>
                                                                        <TableBody>
                                                                                {data.map((row) => (
                                                                                        <TableRow
                                                                                                key={row.id}
                                                                                        >
                                                                                                <TableCell component="th" scope="row">
                                                                                                        {row.firstName}
                                                                                                </TableCell>
                                                                                                <TableCell>{row.lastName}</TableCell>
                                                                                                <TableCell>{row.relationship}</TableCell>
                                                                                                <TableCell><Tooltip title="Edit Dependant"><Button onClick={EditDependant}><EditIcon /></Button></Tooltip><Tooltip title="Delete Dependant"><Button onClick={DeleteDependant}><DeleteIcon /></Button></Tooltip></TableCell>
                                                                                        </TableRow>
                                                                                ))}
                                                                        </TableBody>
                                                                </Table>
                                                        </TableContainer>
                                                </Grid>
                                        </Grid>
                                </Grid>
                        </Box>
                </div>
        );
}

export default Employee
