import * as React from 'react';
import { useState } from 'react';
import { Box } from '@mui/system';
import Grid from '@mui/material/Grid';
import { Button } from '@mui/material';
import TextField from '@mui/material/TextField';
import { Link, useLocation } from 'react-router-dom';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import axios from 'axios';
import { variables } from '../Variables';
import Dependants from './Dependants';





const Employee = (props) => {
        const { state } = useLocation();
        const [employeeFirstName, updateFirstName] = useState(state.employee.firstName);
        const [employeeLastName, updateLastName] = useState(state.employee.firstName);



        const EditEmployee = async (employee) => {
                if (employee == null) {
                        return;
                }

                await axios.put(`${variables.API_URL}/Employee/${employee.id}`, {
                        id: state.employee.id,
                        firstName: employeeFirstName,
                        lastName: employeeLastName,
                        compensationType: state.employee.compensationType
                });
        }


        return (
                <div>
                        <Box component="form"
                                sx={{
                                        '& .MuiTextField-root': { m: 1, width: '25ch' },
                                        'flowGrow': 1
                                }}>
                                <Grid container spacing={2}>
                                        <Grid item xs={12} sx={{ display: 'flex' }}>
                                                <div>
                                                        <Link to="/employees"><Button startIcon={<ArrowBackIcon />} size="large">Go Back</Button></Link>
                                                </div>

                                        </Grid>
                                        <Grid item xs={6}>
                                                <Grid item xs={12}>
                                                        <h1>Edit Employee</h1>
                                                        <TextField
                                                                required
                                                                id="outlined-required"
                                                                label="First Name"
                                                                defaultValue={state.employee.firstName}
                                                                onChange={e => updateFirstName(e.target.value)}
                                                        />
                                                        <TextField
                                                                required
                                                                id="outlined-required"
                                                                label="Last Name"
                                                                defaultValue={state.employee.lastName}
                                                                onChange={e => updateLastName(e.target.value)}
                                                        />
                                                </Grid>
                                                <Grid item xs={12}>
                                                        <Button variant="contained" color="success" size='large' onClick={e => EditEmployee(state.employee)}>Update</Button>
                                                </Grid>
                                        </Grid>
                                        <Grid item xs={6}>
                                                <Dependants employeeId={state.id} />
                                        </Grid>
                                </Grid>
                        </Box>
                </div>
        );
}

export default Employee
