import * as React from 'react';
import { useState, useEffect } from 'react';
import { Navigate, useNavigate } from 'react-router-dom';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import EditIcon from '@mui/icons-material/Edit';
import { styled, Box } from '@mui/system';
import ModalUnstyled from '@mui/base/ModalUnstyled';
import { Button } from '@mui/material';
import TextField from '@mui/material/TextField';
import { Tooltip } from '@mui/material';
import { Stack } from '@mui/material';
import { Select, MenuItem } from '@mui/material';
import axios from 'axios';
import { variables } from '../Variables';
import { useLocation } from 'react-router-dom';

const relationshipTypes = [
    {
        id: 1,
        relationshipType: 'Child'
    },
    {
        id: 2,
        relationshipType: 'Spouse'
    },
    {
        id: 3,
        relationshipType: 'Domestic Partner'
    },
]

const DependantAlter = () => {
    const {state} = useLocation();
    const[newDependant, setDependant] = useState(state.dependant);
    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
        const {name, value} = e.target;
        setDependant({ ...newDependant, [name]: value });
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        alterDependants(newDependant.ModifyType);
    }

    const validate = (e) => {
        if (e.target.value === "") 
            setErrors({...errors, [e.target.name]: true})
        else 
            setErrors({...errors, [e.target.name]: false})
    }

    const alterDependants = async (type) => {
        if (type === "Add") {
            await axios.post(`${variables.API_URL}/Dependant/${newDependant.Id}`, {
                id: 0,
                firstName: newDependant.firstName,
                lastName: newDependant.lastName,
                relationshipType: newDependant.relationshipType
            });
        } else if (type === "Edit") {
            await axios.put(`${variables.API_URL}/Employee/${state.employee.id}`, {
                id: newDependant.id,
                firstName: newDependant.firstName,
                lastName: newDependant.lastName,
                relationshipType: newDependant.relationshipType
            });
        }
    }

    return (
     
        <Box>
            <h1>{newDependant.modifyType} Dependant</h1>
            <form>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <TextField
                        required
                        id="outlined-required"
                        label="First Name"
                        name="firstName"
                        style={errors.title ? { border: "2px solid red" } : null}
                        value={newDependant.firstName}
                        onChange={handleChange}
                        onBlur={validate}
                    />
                </Grid>
                <Grid item xs={12}>
                    <TextField
                        required
                        id="outlined-required"
                        label="Last Name"
                        name="lastName"
                        style={errors.title ? { border: "2px solid red" } : null}
                        value={newDependant.lastName}
                        onChange={handleChange}
                        onBlur={validate}
                    />
                </Grid>
                <Grid item xs={12}>
                    <Select required id="dependant-relationship" name="relationship" value={newDependant.relationship} onChange={handleChange} onBlur={validate}>
                        {relationshipTypes.map((type) => (
                            <MenuItem value={type.id} key={type.id}>{type.relationshipType}</MenuItem>
                        ))}
                    </Select>
                </Grid>
                <Grid item xs={12}>
                        <Button variant="contained" color="success" type="submit">Submit</Button>
                        <Button variant="contained" color="secondary" onClick="">Cancel</Button>
                </Grid>
            </Grid>
            </form>
        </Box>
    );
}

export default DependantAlter;
