import { useLocation } from "react-router-dom";
import { useState } from "react";
import { Box } from "@mui/system";
import { Grid } from "@mui/system";
import { MenuItem, Stack, Button, Select, TextField } from "@mui/material";
import { variables } from "../Variables";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const style = {
    width: 400,
    bgcolor: 'white',
    border: '2px solid #000',
    p: 2,
    px: 4,
    pb: 3,
};

const relationshipTypes = [
    {
        id: 1,
        relationshipType: 'Child'
    },
    {
        id: 2,
        relationshipType: 'Spouse'
    },
    {
        id: 3,
        relationshipType: 'Domestic Partner'
    },
]



const DependantForm = (props) => {
    const { navigate } = useNavigate();
    const { state } = useLocation();
    const [dependant, setDependant] = useState(state.dependant);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setDependant({ ...dependant, [name]: value });
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        validate(dependant);
    }

    const validate = (values) => {

    }

    const AlterDependants = async (type) => {
        if (type === "Add") {
            await axios.post(`${variables.API_URL}/Dependant/${dependant.Id}`, {
                id: 0,
                firstName: dependant.firstName,
                lastName: dependant.lastName,
                relationshipType: dependant.relationshipType
            });
        } else if (type === "Edit") {
            await axios.put(`${variables.API_URL}/Employee/${state.employee.id}`, {
                id: dependant.id,
                firstName: dependant.firstName,
                lastName: dependant.lastName,
                relationshipType: dependant.relationshipType
            });
        }

        navigate(-1)
    }

    return (
        <div>
            <Box sx={style}>
                <h1>{dependant.ModifyType} Dependant</h1>
                <form>
                    <Grid container spacing={3}>
                        <Grid item xs={12}>
                            <TextField
                                required
                                id="outlined-required"
                                label="First Name"
                                name="firstName"
                                value={dependant.firstName}
                                onChange={handleChange}

                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                required
                                id="outlined-required"
                                label="Last Name"
                                name="lastName"
                                value={dependant.lastName}
                                onChange={handleChange}

                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Select id="dependant-relationship" name="relationship" value={dependant.relationship} onChange={handleChange}>
                                {relationshipTypes.map((type) => (
                                    <MenuItem value={type.id} key={type.id}>{type.relationshipType}</MenuItem>
                                ))}
                            </Select>
                        </Grid>
                        <Grid item xs={12}>
                            <Stack direction="row" spacing={4}>
                                <Button variant="contained" color="success" onClick={() => AlterDependants(dependant.ModifyType)}>Update</Button>
                                <Button variant="contained" color="secondary" onClick={() => navigate(-1)}>Cancel</Button>
                            </Stack>
                        </Grid>
                    </Grid>
                </form>
            </Box>
        </div>);
}

export default DependantForm;
