import * as React from 'react';
import { useState, useEffect } from 'react';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import EditIcon from '@mui/icons-material/Edit';
import { styled, Box } from '@mui/system';
import ModalUnstyled from '@mui/base/ModalUnstyled';
import { Button } from '@mui/material';
import TextField from '@mui/material/TextField';
import { Tooltip } from '@mui/material';
import { Stack } from '@mui/material';
import { Select, MenuItem } from '@mui/material';
import axios from 'axios';
import { variables } from '../Variables';
import { useLocation } from 'react-router-dom';

const relationshipTypes = [
    {
        id: 1,
        relationshipType: 'Child'
    },
    {
        id: 2,
        relationshipType: 'Spouse'
    },
    {
        id: 3,
        relationshipType: 'Domestic Partner'
    },
]



const Dependants = () => {
    const { state } = useLocation();
    const [open, setOpen] = useState(false);
    const [dependantData, setData] = useState([]);
    const [dependant, setDependant] = useState({});
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);


    useEffect(() => {
        getDependantData();
    }, []);

    const handleChange = (e) => {
        const {name, value} = e.target;
        setDependant({ ...dependant, [name]: value });
        e.focus();
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        validate(dependant);
    }

    const validate = (values) => {

    }




    const getDependantData = async () => {

        await axios
            .get(`${variables.API_URL}/Dependant/${state.employee.id}`)
            .then((response) => {

                let dependantArr = [];
                dependantArr.push(response.data);

                setData(dependantArr);
            });
    };


    const DeleteDependant = async (targetedId) => {
        await axios.delete(`${variables.API_URL}/Dependant/${targetedId}`);
    }

    const AddDependantModal = () => {
        prepareModal({firstName: "", lastName: "",}, "Add");

    }

    const EditDependantModal = (row) => {
        prepareModal(row, "Edit");
    }

    const AlterDependants = async (type) => {
        if (type === "Add") {
            await axios.post(`${variables.API_URL}/Dependant/${dependant.Id}`, {
                id: 0,
                firstName: dependant.firstName,
                lastName: dependant.lastName,
                relationshipType: dependant.relationshipType
            });
        } else if (type === "Edit") {
            await axios.put(`${variables.API_URL}/Employee/${state.employee.id}`, {
                id: dependant.id,
                firstName: dependant.firstName,
                lastName: dependant.lastName,
                relationshipType: dependant.relationshipType
            });
        }

        getDependantData();
    }

    function prepareModal(modalRow, type) {
        modalRow["ModifyType"] = type;
        setDependant(modalRow);
        handleOpen();
    }

    const StyledModal = styled(ModalUnstyled)`
  position: fixed;
  z-index: 1300;
  right: 0;
  bottom: 0;
  top: 0;
  left: 0;
  display: flex;
  align-items: center;
  justify-content: center;
`;

    const Backdrop = styled('div')`
  z-index: -1;
  position: fixed;
  right: 0;
  bottom: 0;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.5);
  -webkit-tap-highlight-color: transparent;
`;

    const style = {
        width: 400,
        bgcolor: 'white',
        border: '2px solid #000',
        p: 2,
        px: 4,
        pb: 3,
    };

    return (
        <div>
            <StyledModal
                aria-labelledby="unstyled-modal-title"
                aria-describedby="unstyled-modal-description"
                open={open}
                onClose={handleClose}
                BackdropComponent={Backdrop}
            >
                <Box sx={style}>
                    <h1>{dependant.ModifyType} Dependant</h1>
                    <Grid container spacing={3}>
                        <Grid item xs={12}>
                            <TextField
                                required
                                id="outlined-required"
                                label="First Name"
                                name="firstName"
                                value={dependant.firstName}
                                onChange={handleChange}
                                
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                required
                                id="outlined-required"
                                label="Last Name"
                                name="lastName"
                                value={dependant.lastName}
                                onChange={handleChange}
                                
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <Select id="dependant-relationship" name="relationship" value={dependant.relationship} onChange={handleChange}>
                                {relationshipTypes.map((type) => (
                                    <MenuItem value={type.id} key={type.id}>{type.relationshipType}</MenuItem>
                                ))}
                            </Select>
                        </Grid>
                        <Grid item xs={12}>
                            <Stack direction="row" spacing={4}>
                                <Button variant="contained" color="success" onClick={() => AlterDependants(dependant.ModifyType)}>Update</Button>
                                <Button variant="contained" color="secondary" onClick={handleClose}>Cancel</Button>
                            </Stack>
                        </Grid>
                    </Grid>
                </Box>
            </StyledModal>
            <Grid item xs={12}>
                <h2>Dependants <Tooltip title="Add Dependant"><Button onClick={AddDependantModal} sx={{ float: 'right', marginRight: "20px" }}><AddCircleIcon color="success" fontSize='large' /></Button></Tooltip> </h2>
            </Grid>
            <Grid item xs={12}>
                <TableContainer component={Paper}>
                    <Table sx={{ minWidth: 650 }} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>First Name</TableCell>
                                <TableCell>Last Name</TableCell>
                                <TableCell>Relationship</TableCell>
                                <TableCell>Options</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {dependantData.length ? dependantData.map((row) => (
                                <TableRow
                                    key={row.id}
                                >
                                    <TableCell component="th" scope="row">
                                        {row.firstName}
                                    </TableCell>
                                    <TableCell>{row.lastName}</TableCell>
                                    <TableCell>{row.relationship}</TableCell>
                                    <TableCell><Tooltip title="Edit Dependant"><Button onClick={e => EditDependantModal(row)}><EditIcon /></Button></Tooltip><Tooltip title="Delete Dependant"><Button onClick={DeleteDependant}><DeleteIcon /></Button></Tooltip></TableCell>
                                </TableRow>
                            )) : <TableRow><TableCell>No Records found</TableCell></TableRow>}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Grid>
        </div>
    )
}

export default Dependants;
